 // DEPENDENCIES
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { Examination } from '../../domain/regcovl_db/examination';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../examination.service.ts
 */

/*
 * SCHEMA DB examination
 *
	{
		score: {
			type: 'Number'
		},
		type: {
			type: 'String'
		},
		valid: {
			type: 'Boolean'
		},
		//RELATIONS
		//EXTERNAL RELATIONS
		_course: {
			type: Schema.ObjectId,
			ref : "examination"
		},
		_student: {
			type: Schema.ObjectId,
			ref : "examination"
		},
	}
 *
 */
@Injectable()
export class ExaminationBaseService {

    contextUrl: string = environment.endpoint + '/examination';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * examinationService.create
    *   @description CRUD ACTION create
    *
    */
    create(item: Examination): Observable<Examination> {
        return this.http
            .post<Examination>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * examinationService.delete
    *   @description CRUD ACTION delete
    *   @param ObjectId id Id
    *
    */
    remove(id: string): Observable<void> {
        return this.http
            .delete<void>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * examinationService.findBy_course
    *   @description CRUD ACTION findBy_course
    *   @param Objectid key Id of model to search for
    *
    */
    findBy_course(id: string): Observable<Examination[]> {
        return this.http
            .get<Examination[]>(this.contextUrl + '/findBy_course/' + id)
            .pipe(
                map(response => response)
            );
    }

    /**
    * examinationService.findBy_student
    *   @description CRUD ACTION findBy_student
    *   @param Objectid key Id of model to search for
    *
    */
    findBy_student(id: string): Observable<Examination[]> {
        return this.http
            .get<Examination[]>(this.contextUrl + '/findBy_student/' + id)
            .pipe(
                map(response => response)
            );
    }

    /**
    * examinationService.get
    *   @description CRUD ACTION get
    *   @param ObjectId id Id resource
    *
    */
    get(id: string): Observable<Examination> {
        return this.http
            .get<Examination>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * examinationService.list
    *   @description CRUD ACTION list
    *
    */
    list(): Observable<Examination[]> {
        return this.http
            .get<Examination[]>(this.contextUrl)
            .pipe(map(data => data));
    }

    /**
    * examinationService.update
    *   @description CRUD ACTION update
    *   @param ObjectId id Id
    *
    */
    update(item: Examination): Observable<Examination> {
        return this.http
            .post<Examination>(this.contextUrl + '/' + item._id, item)
            .pipe(map(data => data));
    }


    // Custom APIs


    /**
    * examinationService.validate
    *   @description This API is used to validate the exam
    *   @param String id id of the exam
    *   @returns Boolean
    *
    */
    validate(...params: any[]): Observable<any> {
        return this.http
            .post<any>(this.contextUrl + '/{id}/validate', {})
            .pipe(
                map(response => response)
            );
    }

}
